import React from 'react';
import styled from 'styled-components';


 const ContainerStyle = styled.div`
    display: flex;
    align-items: center;
    border: 1px solid black;
    padding: 20px 10px;
    margin-bottom: 10px;
    height: 80px;
`
const ContainerStyleDiv = styled.div`
    display: flex;
    flex-direction: column;
    justify-items: flex-start;
`
const EstiloImg = styled.img`
    width: 70px;
    margin-right: 10px;
    border-radius: 50%;
`
function CardPequeno(props) {
    return (
        <ContainerStyle>
            <EstiloImg src={ props.imagem } />
            <ContainerStyleDiv>
                <h4>{ props.nome }</h4>
                <p>{ props.descricao }</p>
            </ContainerStyleDiv>
        </ContainerStyle>
    )
}

export default CardPequeno;
