import React from 'react';
import styled from 'styled-components';

const CardGrandeStyle = styled.div`
    display: flex;
    align-items: center;
    border: 1px solid black;
    padding: 20px 10px;
    margin-bottom: 10px;
    height: auto;

`
const CardGrandeStyleImg = styled.img`
    width: 70px;
    margin-right: 10px;
    border-radius: 50%;
`

const CardGrandeStyleH4 = styled.h4`
    margin-bottom: 15px;
`

const CardGrandeStyleDiv = styled.div`
    display: flex;
    flex-direction: column;
    justify-items: flex-start;
`

function CardGrande(props) {
    return (
        <CardGrandeStyle className="bigcard-container">
            <CardGrandeStyleImg src={ props.imagem } />
            <CardGrandeStyleDiv>
                <CardGrandeStyleH4>{ props.nome }</CardGrandeStyleH4>
                <p>{ props.descricao }</p>
            </CardGrandeStyleDiv>
        </CardGrandeStyle>
    )
}

export default CardGrande;
