import React from 'react';
import styled from 'styled-components';
import './CardPequeno.css';

export const estiloDiv = styled.div`
display: flex;
    align-items: center;
    border: 1px solid black;
    padding: 20px 10px;
    margin-bottom: 10px;
    height: 120px;
`
export const estiloImg = styled.estiloDiv.img`
    width: 70px;
    margin-right: 10px;
    border-radius: 50%;
`
function CardPequeno(props) {
    return (
        <estiloDiv className="smallcard-container">
            <img src={ props.imagem } />
            <div>
                <h4>{ props.nome }</h4>
                <p>{ props.descricao }</p>
            </div>
        </estiloDiv>
    )
}

export default CardPequeno;
